-- ‚îÄ‚îÄ‚îÄ Lesson 1.4 ‚Äî Crime Scene Review (Checkpoint) ‚îÄ‚îÄ‚îÄ
INSERT INTO course_lessons (id, unit_id, title, description, emoji, sort_order, xp_reward, is_checkpoint_review) VALUES
('d4000000-0000-0000-0000-000000000004', 'b4000000-0000-0000-0000-000000000001', 'Crime Scene Review', 'Test your knowledge of crime scene processing fundamentals.', '‚úÖ', 4, 15, true);

INSERT INTO applets (id, type, title, question, hint, explanation, content, difficulty, tags) VALUES
('a4040000-0000-0000-0000-000000000001', 'thought-tree', 'Scene Assessment', 'Work through this decision tree to correctly process a fresh homicide scene.', 'Think step by step ‚Äî safety, then containment, then documentation.',
'This decision tree mirrors real first-responder training. Every branch reinforces the core principle: rushing to collect evidence before the scene is safe and documented is the number one rookie mistake. Detectives who skip steps create openings for defense attorneys. The correct path always prioritizes safety ‚Üí perimeter ‚Üí documentation ‚Üí collection, in that exact order.',
'{"nodes":[{"id":"n1","question":"You arrive at a potential homicide. What''s your FIRST action?","leftChoice":{"id":"n1a","text":"Ensure scene safety ‚Äî check for threats"},"rightChoice":{"id":"n1b","text":"Start collecting evidence immediately"},"correctChoiceId":"n1a"},{"id":"n2","question":"Scene is safe. Backup is en route. What next?","leftChoice":{"id":"n2a","text":"Establish perimeter with crime scene tape"},"rightChoice":{"id":"n2b","text":"Start interviewing the person who found the body"},"correctChoiceId":"n2a"},{"id":"n3","question":"Perimeter is secure. How do you begin documentation?","leftChoice":{"id":"n3a","text":"Overall photos first, then work inward"},"rightChoice":{"id":"n3b","text":"Close-up photos of the weapon first"},"correctChoiceId":"n3a"},{"id":"n4","question":"You notice the victim may have been moved. What clue told you?","leftChoice":{"id":"n4a","text":"Lividity doesn''t match body position"},"rightChoice":{"id":"n4b","text":"The victim''s eyes are open"},"correctChoiceId":"n4a"},{"id":"n5","question":"Evidence collection is starting. What must you maintain throughout?","leftChoice":{"id":"n5a","text":"Chain of custody for every item"},"rightChoice":{"id":"n5b","text":"A running tally of suspect names"},"correctChoiceId":"n5a"}],"finalAnswer":"You correctly processed the scene: safety ‚Üí perimeter ‚Üí documentation ‚Üí body assessment ‚Üí evidence collection with chain of custody. This is the foundation of every successful homicide investigation."}', 2, ARRAY['forensics','crime-scene','review']),

('a4040000-0000-0000-0000-000000000002', 'fill-blanks', 'Crime Scene Principles', 'Complete the key crime scene investigation principles.', 'Think about what protects evidence in court.',
'These are foundational principles taught at every police academy. The golden hour emphasizes urgency. Chain of custody protects evidence integrity. Locard''s Exchange Principle ‚Äî that every contact leaves a trace ‚Äî is the philosophical basis of forensic science. And the wide-to-narrow photography protocol ensures context is preserved before detail is captured.',
'{"segments":[{"type":"text","content":"The first 60 minutes after discovery is called the "},{"type":"slot","slotId":"s1","correctAnswerId":"a1"},{"type":"text","content":". Every piece of evidence must have an unbroken "},{"type":"slot","slotId":"s2","correctAnswerId":"a2"},{"type":"text","content":". According to Locard''s Principle, every contact leaves a "},{"type":"slot","slotId":"s3","correctAnswerId":"a3"},{"type":"text","content":". Crime scene photos progress from "},{"type":"slot","slotId":"s4","correctAnswerId":"a4"},{"type":"text","content":" to close-up."}],"answerBlocks":[{"id":"a1","content":"golden hour"},{"id":"a2","content":"chain of custody"},{"id":"a3","content":"trace"},{"id":"a4","content":"wide"},{"id":"d1","content":"silver minute"},{"id":"d2","content":"evidence bag"},{"id":"d3","content":"witness"},{"id":"d4","content":"narrow"}]}', 2, ARRAY['forensics','crime-scene','principles']);

