-- ‚îÄ‚îÄ‚îÄ Lesson 2.2 ‚Äî Fingerprints & Impressions ‚îÄ‚îÄ‚îÄ
INSERT INTO course_lessons (id, unit_id, title, description, emoji, sort_order, xp_reward) VALUES
('d4000000-0000-0000-0000-000000000006', 'b4000000-0000-0000-0000-000000000002', 'Fingerprints & Impressions', 'The original biometric ‚Äî how prints are found, lifted, and matched.', 'üëÜ', 2, 10);

INSERT INTO applets (id, type, title, question, hint, explanation, content, difficulty, tags) VALUES
('a4060000-0000-0000-0000-000000000001', 'categorization-grid', 'Fingerprint Types', 'Classify each fingerprint pattern into its correct category.', 'The three main categories are loops, whorls, and arches.',
'Fingerprint classification has been used since the late 1800s. Loops (60-65% of people) have ridges that enter from one side, curve, and exit the same side ‚Äî ulnar loops curve toward the pinky, radial loops toward the thumb. Whorls (30-35%) have circular or spiral patterns with at least two deltas (triangular points). Arches (5%) have ridges flowing from one side to the other with a rise in the middle ‚Äî tented arches have a sharp spike, plain arches are smooth. No two people have identical prints ‚Äî even identical twins have different fingerprints due to random fetal development.',
'{"categories":[{"id":"loop","label":"Loop","emoji":"üîÑ"},{"id":"whorl","label":"Whorl","emoji":"üåÄ"},{"id":"arch","label":"Arch","emoji":"‚õ∞Ô∏è"}],"items":[{"id":"i1","text":"Ridges enter and exit the same side"},{"id":"i2","text":"Circular/spiral pattern with two deltas"},{"id":"i3","text":"Ridges flow side to side with a rise"},{"id":"i4","text":"Most common pattern (~65% of people)"},{"id":"i5","text":"Accounts for ~30% of all fingerprints"},{"id":"i6","text":"Rarest pattern (~5% of people)"}],"correctMapping":{"i1":"loop","i2":"whorl","i3":"arch","i4":"loop","i5":"whorl","i6":"arch"},"layout":"columns"}', 2, ARRAY['forensics','fingerprints','classification']),

('a4060000-0000-0000-0000-000000000002', 'ordering', 'Fingerprint Processing', 'Order the steps to process a latent (invisible) fingerprint from a crime scene surface.', 'You need to find it, develop it, lift it, then match it.',
'Latent fingerprints are invisible to the naked eye ‚Äî left by natural oils and sweat on fingers. First, the surface is examined with alternate light sources (UV, laser) which can make prints fluoresce. Then a development technique is chosen based on the surface: powder dusting for smooth surfaces, superglue fuming for plastics, ninhydrin spray for paper. The developed print is photographed in place (in case lifting fails). Then it''s lifted with adhesive tape and placed on a contrast card. Finally, it''s scanned into AFIS (Automated Fingerprint Identification System) for database comparison.',
'{"items":[{"id":"i1","label":"Examine surface with alternate light source","emoji":"üî¶"},{"id":"i2","label":"Apply development technique (powder/chemical)","emoji":"üß™"},{"id":"i3","label":"Photograph the developed print in place","emoji":"üì∏"},{"id":"i4","label":"Lift print with adhesive tape","emoji":"üìã"},{"id":"i5","label":"Scan into AFIS database for matching","emoji":"üíª"}],"correctOrder":["i1","i2","i3","i4","i5"],"direction":"top-down"}', 2, ARRAY['forensics','fingerprints','processing']),

('a4060000-0000-0000-0000-000000000003', 'mcq', 'Print Identification', 'How many matching minutiae points do most US jurisdictions require to declare a fingerprint match?', 'There''s actually no universal standard in the US...',
'Surprisingly, there is no universal legal standard in the United States for the minimum number of matching minutiae (ridge endings, bifurcations, dots) required for a fingerprint identification. The UK uses 16 points, some European countries use 12, but US courts generally leave it to the examiner''s expert judgment. Most US examiners use 8-12 points as a practical guideline. This subjectivity has been criticized ‚Äî a 2009 National Academy of Sciences report found that fingerprint analysis is less objective than commonly believed, and wrongful convictions have resulted from misidentifications.',
'{"options":[{"id":"a","text":"No universal standard ‚Äî typically 8-12 at examiner discretion"},{"id":"b","text":"Exactly 7 points ‚Äî set by the FBI"},{"id":"c","text":"At least 20 points ‚Äî to ensure certainty"},{"id":"d","text":"Just 3 points ‚Äî the legal minimum since 1990"}],"correctOptionId":"a"}', 3, ARRAY['forensics','fingerprints','legal']);

INSERT INTO lesson_applets (lesson_id, applet_id, sort_order) VALUES
('d4000000-0000-0000-0000-000000000006', 'a4060000-0000-0000-0000-000000000001', 1),
