-- ‚îÄ‚îÄ‚îÄ Lesson 2.4 ‚Äî Forensic Evidence Review (Checkpoint) ‚îÄ‚îÄ‚îÄ
INSERT INTO course_lessons (id, unit_id, title, description, emoji, sort_order, xp_reward, is_checkpoint_review) VALUES
('d4000000-0000-0000-0000-000000000008', 'b4000000-0000-0000-0000-000000000002', 'Forensic Evidence Review', 'Test your knowledge of DNA, fingerprints, and ballistics.', '‚úÖ', 4, 15, true);

INSERT INTO applets (id, type, title, question, hint, explanation, content, difficulty, tags) VALUES
('a4080000-0000-0000-0000-000000000001', 'thought-tree', 'Evidence Analysis', 'Work through this decision tree to correctly analyze forensic evidence found at a scene.', 'Match the evidence type to the right analysis method.',
'This decision tree reinforces the connection between evidence type and analytical approach. DNA goes to the biology lab. Fingerprints go to the latent print unit. Ballistics go to the firearms section. Each type of evidence requires specialized equipment, trained examiners, and different analytical techniques. Sending evidence to the wrong lab wastes time ‚Äî and in murder cases, time is the enemy.',
'{"nodes":[{"id":"n1","question":"You find a blood stain at the crime scene. What''s the first lab analysis?","leftChoice":{"id":"n1a","text":"DNA extraction and STR profiling"},"rightChoice":{"id":"n1b","text":"Fingerprint analysis with AFIS"},"correctChoiceId":"n1a"},{"id":"n2","question":"A bullet is recovered from the wall. Where does it go?","leftChoice":{"id":"n2a","text":"Firearms lab for rifling comparison"},"rightChoice":{"id":"n2b","text":"DNA lab for touch DNA on the bullet"},"correctChoiceId":"n2a"},{"id":"n3","question":"A suspect says they never touched the weapon. How do you test this?","leftChoice":{"id":"n3a","text":"Test their hands for gunshot residue (GSR)"},"rightChoice":{"id":"n3b","text":"Give them a lie detector test"},"correctChoiceId":"n3a"},{"id":"n4","question":"A partial fingerprint is found on a glass at the scene. What system do you search?","leftChoice":{"id":"n4a","text":"AFIS ‚Äî Automated Fingerprint Identification System"},"rightChoice":{"id":"n4b","text":"CODIS ‚Äî Combined DNA Index System"},"correctChoiceId":"n4a"},{"id":"n5","question":"DNA results show a match probability of 1 in 10 billion. What does this mean?","leftChoice":{"id":"n5a","text":"Extremely strong ‚Äî virtually unique identification"},"rightChoice":{"id":"n5b","text":"Too weak ‚Äî need at least 1 in 1 trillion"},"correctChoiceId":"n5a"}],"finalAnswer":"You correctly routed each evidence type to the right analysis: DNA ‚Üí STR profiling, bullets ‚Üí firearms lab, hand residue ‚Üí GSR test, fingerprints ‚Üí AFIS, and correctly interpreted DNA match statistics. This is how real forensic labs operate."}', 3, ARRAY['forensics','evidence','review']),

('a4080000-0000-0000-0000-000000000002', 'fill-blanks', 'Forensic Databases', 'Complete the names and purposes of key forensic databases.', 'Each evidence type has its own national database.',
'The three major forensic databases in the US work independently but collectively help solve cases. CODIS (Combined DNA Index System) holds DNA profiles from convicted offenders and crime scenes ‚Äî it has helped solve over 500,000 cases since 1998. AFIS (now called NGI ‚Äî Next Generation Identification) holds fingerprints. NIBIN connects bullets and casings across different crime scenes, revealing serial shooters or shared weapons. When these databases "hit," they can link crimes across states that would otherwise never be connected.',
'{"segments":[{"type":"text","content":"The FBI''s DNA database is called "},{"type":"slot","slotId":"s1","correctAnswerId":"a1"},{"type":"text","content":". Fingerprints are searched through "},{"type":"slot","slotId":"s2","correctAnswerId":"a2"},{"type":"text","content":". Bullets and casings are matched using "},{"type":"slot","slotId":"s3","correctAnswerId":"a3"},{"type":"text","content":". A DNA match probability of 1 in 10 billion means the evidence is "},{"type":"slot","slotId":"s4","correctAnswerId":"a4"},{"type":"text","content":" identification."}],"answerBlocks":[{"id":"a1","content":"CODIS"},{"id":"a2","content":"AFIS"},{"id":"a3","content":"NIBIN"},{"id":"a4","content":"virtually unique"},{"id":"d1","content":"NCIC"},{"id":"d2","content":"VICAP"},{"id":"d3","content":"ViCAP"},{"id":"d4","content":"inconclusive"}]}', 3, ARRAY['forensics','databases','review']);

