-- =================================================================
-- UNIT 3 ‚Äî The Investigation (Building a Case)
-- =================================================================
INSERT INTO course_units (id, course_id, title, description, sort_order) VALUES
('b4000000-0000-0000-0000-000000000003', 'c0000000-0000-0000-0000-000000000005', 'The Investigation', 'Suspects, motives, alibis, and interrogations ‚Äî building a case that holds up in court.', 3);

-- ‚îÄ‚îÄ‚îÄ Lesson 3.1 ‚Äî Suspects & Motives ‚îÄ‚îÄ‚îÄ
INSERT INTO course_lessons (id, unit_id, title, description, emoji, sort_order, xp_reward) VALUES
('d4000000-0000-0000-0000-000000000009', 'b4000000-0000-0000-0000-000000000003', 'Suspects & Motives', 'The classic question: who had means, motive, and opportunity?', 'üéØ', 1, 10);

INSERT INTO applets (id, type, title, question, hint, explanation, content, difficulty, tags) VALUES
('a4090000-0000-0000-0000-000000000001', 'categorization-grid', 'The Murder Triangle', 'Categorize each factor as Means, Motive, or Opportunity.', 'Means = ability to kill, Motive = reason to kill, Opportunity = chance to kill.',
'The "murder triangle" (means, motive, opportunity) is the foundational framework for suspect evaluation. Means is the physical ability ‚Äî do they own a gun, are they strong enough, do they have access to poison? Motive is the psychological driver ‚Äî revenge, money, jealousy, silencing a witness. Opportunity is the practical chance ‚Äî were they at the location, did they have time, do they lack an alibi? A strong suspect has all three. Missing even one element weakens the case significantly, which is why defense attorneys focus on demolishing whichever leg is weakest.',
'{"categories":[{"id":"means","label":"Means","emoji":"üîß"},{"id":"motive","label":"Motive","emoji":"üí≠"},{"id":"opportunity","label":"Opportunity","emoji":"‚è∞"}],"items":[{"id":"i1","text":"Suspect owns same caliber gun as murder weapon"},{"id":"i2","text":"Victim was about to change their will"},{"id":"i3","text":"Suspect has no alibi for time of death"},{"id":"i4","text":"Suspect has military combat training"},{"id":"i5","text":"Suspect''s key card accessed the building at 11 PM"},{"id":"i6","text":"$2 million life insurance policy on the victim"}],"correctMapping":{"i1":"means","i2":"motive","i3":"opportunity","i4":"means","i5":"opportunity","i6":"motive"},"layout":"columns"}', 2, ARRAY['investigation','suspects','motive']),

('a4090000-0000-0000-0000-000000000002', 'ordering', 'Suspect Prioritization', 'Rank these investigative steps from first to last when developing suspects.', 'Start with who was closest to the victim, then expand outward.',
'Homicide investigation follows concentric circles outward from the victim. Victimology comes first ‚Äî understanding the victim''s life, relationships, enemies, and routines. Statistically, 64% of murder victims are killed by someone they know. The inner circle (spouse, family, close associates) is investigated first because they have the strongest combination of means, motive, and opportunity. Only after clearing the inner circle do detectives expand to acquaintances, then strangers. Witness canvass and surveillance footage help establish timelines. Phone and financial records reveal hidden connections.',
'{"items":[{"id":"i1","label":"Victimology ‚Äî study the victim''s life and relationships","emoji":"üë§"},{"id":"i2","label":"Inner circle ‚Äî interview spouse, family, close friends","emoji":"üë®‚Äçüë©‚Äçüëß"},{"id":"i3","label":"Canvass ‚Äî interview neighbors and witnesses","emoji":"üèòÔ∏è"},{"id":"i4","label":"Records ‚Äî pull phone, financial, and digital data","emoji":"üì±"},{"id":"i5","label":"Expand ‚Äî investigate acquaintances and strangers","emoji":"üåê"}],"correctOrder":["i1","i2","i3","i4","i5"],"direction":"top-down"}', 2, ARRAY['investigation','suspects','procedure']),

('a4090000-0000-0000-0000-000000000003', 'mcq', 'Homicide Statistics', 'According to FBI statistics, what percentage of murder victims knew their killer?', 'Most murders are not committed by strangers.',
'FBI Uniform Crime Report data consistently shows that approximately 64% of murder victims knew their killer ‚Äî whether a family member, intimate partner, friend, or acquaintance. About 15% are killed by strangers. The remaining ~21% involve unknown relationships (unsolved cases). This is why detectives always start with the victim''s inner circle. Intimate partner homicides are particularly common ‚Äî domestic violence is the #1 risk factor for being murdered. The phrase "look at the husband/wife first" is a statistical reality, not just a TV trope.',
'{"options":[{"id":"a","text":"About 64% ‚Äî most victims knew their killer"},{"id":"b","text":"About 30% ‚Äî most murders are by strangers"},{"id":"c","text":"About 90% ‚Äî nearly all murders are by family"},{"id":"d","text":"About 50% ‚Äî it''s an even split"}],"correctOptionId":"a"}', 2, ARRAY['investigation','statistics','motive']);

INSERT INTO lesson_applets (lesson_id, applet_id, sort_order) VALUES
('d4000000-0000-0000-0000-000000000009', 'a4090000-0000-0000-0000-000000000001', 1),
('d4000000-0000-0000-0000-000000000009', 'a4090000-0000-0000-0000-000000000002', 2),
('d4000000-0000-0000-0000-000000000009', 'a4090000-0000-0000-0000-000000000003', 3);

-- ‚îÄ‚îÄ‚îÄ Lesson 3.2 ‚Äî Interrogation Techniques ‚îÄ‚îÄ‚îÄ
INSERT INTO course_lessons (id, unit_id, title, description, emoji, sort_order, xp_reward) VALUES
('d4000000-0000-0000-0000-000000000010', 'b4000000-0000-0000-0000-000000000003', 'Interrogation Techniques', 'The psychology of getting suspects to talk ‚Äî and the fine line with false confessions.', 'üó£Ô∏è', 2, 10);

INSERT INTO applets (id, type, title, question, hint, explanation, content, difficulty, tags) VALUES
('a4100000-0000-0000-0000-000000000001', 'match-pairs', 'Interrogation Methods', 'Match each interrogation technique to its description.', 'Some are designed to build rapport, others to create psychological pressure.',
'Interrogation techniques range from rapport-based (PEACE model, used in the UK) to confrontational (Reid Technique, common in the US). The Reid Technique uses behavioral analysis, theme development, and minimization ("anyone would have done the same") to encourage confession. The PEACE model focuses on information gathering rather than confession extraction. The cognitive interview helps witnesses (not suspects) recall details through mental reinstatement. Minimization/maximization are psychological tactics ‚Äî minimizing moral blame or maximizing consequences ‚Äî that studies show can increase false confessions in vulnerable individuals.',
'{"leftItems":[{"id":"l1","text":"Reid Technique"},{"id":"l2","text":"PEACE Model"},{"id":"l3","text":"Cognitive Interview"},{"id":"l4","text":"Minimization"},{"id":"l5","text":"Behavioral Analysis"}],"rightItems":[{"id":"r1","text":"Confrontational ‚Äî uses theme development to seek confessions"},{"id":"r2","text":"Non-confrontational ‚Äî focuses on gathering information, not confessions"},{"id":"r3","text":"Helps witnesses recall details through mental reinstatement of context"},{"id":"r4","text":"Downplays moral blame ‚Äî \"anyone would have done the same thing\""},{"id":"r5","text":"Reading body language and verbal cues for deception indicators"}],"correctPairs":{"l1":"r1","l2":"r2","l3":"r3","l4":"r4","l5":"r5"},"leftColumnLabel":"Technique","rightColumnLabel":"Description"}', 3, ARRAY['investigation','interrogation','psychology']),

('a4100000-0000-0000-0000-000000000002', 'mcq', 'False Confessions', 'What percentage of DNA exonerations (proven wrongful convictions) involved false confessions?', 'It''s disturbingly higher than most people assume.',
'According to the Innocence Project, approximately 25-30% of DNA exonerations involved false confessions or admissions. This means roughly 1 in 4 people proven innocent by DNA had previously confessed to a crime they didn''t commit. Risk factors include youth (juvenile suspects are especially vulnerable), intellectual disability, mental illness, sleep deprivation, and lengthy interrogations. The Central Park Five case (1989) is a landmark example ‚Äî five teenagers falsely confessed to a brutal assault after hours of interrogation. DNA later identified the actual perpetrator. This has driven major reforms in interrogation recording requirements.',
'{"options":[{"id":"a","text":"About 25-30% ‚Äî roughly 1 in 4 wrongful conviction cases"},{"id":"b","text":"Less than 1% ‚Äî false confessions are extremely rare"},{"id":"c","text":"About 5% ‚Äî a small but significant minority"},{"id":"d","text":"Over 75% ‚Äî the vast majority of wrongful convictions"}],"correctOptionId":"a"}', 3, ARRAY['investigation','false-confessions','innocence']),

('a4100000-0000-0000-0000-000000000003', 'thought-tree', 'Interrogation Ethics', 'Navigate the ethical dilemmas of interrogation by making the right decisions.', 'Consider both effectiveness and the risk of false confessions.',
'Each decision in this tree reflects real debates in law enforcement. Recording protects both the suspect and the detective. Juvenile suspects need special protections because adolescent brains are more susceptible to coercion. Sleep-deprived suspects produce unreliable statements. The right to attorney is constitutional ‚Äî deliberately circumventing it can get the entire confession thrown out and lead to civil liability. These aren''t just ethical ideals ‚Äî they''re practical necessities for building cases that survive court challenges.',
'{"nodes":[{"id":"n1","question":"Should interrogations be recorded on video from start to finish?","leftChoice":{"id":"n1a","text":"Yes ‚Äî protects both suspect and detective"},"rightChoice":{"id":"n1b","text":"No ‚Äî cameras make suspects less likely to talk"},"correctChoiceId":"n1a"},{"id":"n2","question":"A 15-year-old suspect asks for their parent. The detective wants to continue. What''s right?","leftChoice":{"id":"n2a","text":"Stop ‚Äî juvenile suspects need guardian or attorney present"},"rightChoice":{"id":"n2b","text":"Continue ‚Äî they already waived Miranda"},"correctChoiceId":"n2a"},{"id":"n3","question":"It''s been 14 hours of interrogation. The suspect is exhausted. Continue?","leftChoice":{"id":"n3a","text":"Stop ‚Äî fatigue increases false confession risk"},"rightChoice":{"id":"n3b","text":"Push harder ‚Äî they''re about to break"},"correctChoiceId":"n3a"},{"id":"n4","question":"A suspect asks for a lawyer. What must happen immediately?","leftChoice":{"id":"n4a","text":"All questioning stops until attorney is present"},"rightChoice":{"id":"n4b","text":"Finish the current line of questions first"},"correctChoiceId":"n4a"},{"id":"n5","question":"Which model prioritizes information-gathering over confession-seeking?","leftChoice":{"id":"n5a","text":"PEACE model"},"rightChoice":{"id":"n5b","text":"Reid Technique"},"correctChoiceId":"n5a"}],"finalAnswer":"You navigated the ethics correctly: record everything, protect juveniles, prevent fatigue-driven false confessions, honor attorney requests immediately, and prefer information-gathering approaches. Ethical interrogation produces more reliable results AND survives court challenges."}', 3, ARRAY['investigation','interrogation','ethics']);

INSERT INTO lesson_applets (lesson_id, applet_id, sort_order) VALUES
('d4000000-0000-0000-0000-000000000010', 'a4100000-0000-0000-0000-000000000001', 1),
('d4000000-0000-0000-0000-000000000010', 'a4100000-0000-0000-0000-000000000002', 2),
('d4000000-0000-0000-0000-000000000010', 'a4100000-0000-0000-0000-000000000003', 3);

-- ‚îÄ‚îÄ‚îÄ Lesson 3.3 ‚Äî Digital Forensics ‚îÄ‚îÄ‚îÄ
INSERT INTO course_lessons (id, unit_id, title, description, emoji, sort_order, xp_reward) VALUES
('d4000000-0000-0000-0000-000000000011', 'b4000000-0000-0000-0000-000000000003', 'Digital Forensics', 'Phones, computers, and the internet ‚Äî the modern crime scene.', 'üíª', 3, 10);

INSERT INTO applets (id, type, title, question, hint, explanation, content, difficulty, tags) VALUES
('a4110000-0000-0000-0000-000000000001', 'ordering', 'Digital Evidence Handling', 'Order the steps for handling a suspect''s smartphone found at a crime scene.', 'You need to preserve the data before the phone locks or gets remotely wiped.',
'Digital evidence is incredibly volatile. A smartphone at a crime scene can be remotely wiped, can auto-lock, or can connect to networks that alter data. Step one is always putting the device in airplane mode or a Faraday bag to block signals. Photography documents the device''s state (was the screen on? what app was open?). The device goes to a forensic lab where a certified examiner creates a bit-for-bit forensic image ‚Äî an exact copy of all data including deleted files. Analysis happens on the copy, never the original. Only then is the evidence extracted using tools like Cellebrite or GrayKey.',
'{"items":[{"id":"i1","label":"Place in Faraday bag (block all signals)","emoji":"üì±"},{"id":"i2","label":"Photograph the device and screen state","emoji":"üì∏"},{"id":"i3","label":"Transport to digital forensics lab","emoji":"üî¨"},{"id":"i4","label":"Create forensic image (bit-for-bit copy)","emoji":"üíæ"},{"id":"i5","label":"Extract and analyze data from the copy","emoji":"üîç"}],"correctOrder":["i1","i2","i3","i4","i5"],"direction":"top-down"}', 3, ARRAY['forensics','digital','phone']),

('a4110000-0000-0000-0000-000000000002', 'categorization-grid', 'Digital Evidence Types', 'Categorize each type of digital evidence by its source.', 'Think about where this data lives ‚Äî on the device, with the carrier, or online.',
'Digital evidence comes from three main sources. Device data is stored locally on the phone/computer: texts, photos, app data, GPS logs, deleted files (often recoverable). Carrier records are held by the phone company: call logs, cell tower pings (location), text metadata (who you texted and when, but not content). Internet/cloud data is held by service providers: emails, social media posts, search history, cloud backups. Each requires different legal processes ‚Äî device searches need a warrant, carrier records need a court order, and cloud data may need a warrant directed at the provider.',
'{"categories":[{"id":"device","label":"On Device","emoji":"üì±"},{"id":"carrier","label":"Carrier Records","emoji":"üì°"},{"id":"cloud","label":"Internet/Cloud","emoji":"‚òÅÔ∏è"}],"items":[{"id":"i1","text":"Deleted photos recoverable from storage"},{"id":"i2","text":"Cell tower pings showing location history"},{"id":"i3","text":"Google search history for \"how to\""},{"id":"i4","text":"Text messages stored on the phone"},{"id":"i5","text":"Call duration and frequency logs"},{"id":"i6","text":"Social media direct messages"}],"correctMapping":{"i1":"device","i2":"carrier","i3":"cloud","i4":"device","i5":"carrier","i6":"cloud"},"layout":"columns"}', 3, ARRAY['forensics','digital','evidence-types']),

('a4110000-0000-0000-0000-000000000003', 'mcq', 'Cell Tower Evidence', 'A suspect''s phone pinged three cell towers at 10 PM, 10:15 PM, and 10:30 PM, each closer to the crime scene. What does this suggest?', 'Cell phones connect to the nearest tower with the strongest signal.',
'Cell tower analysis (sometimes called CSLI ‚Äî Cell Site Location Information) tracks a phone''s approximate location by recording which tower it connects to. A progression of tower pings moving toward the crime scene creates a travel timeline ‚Äî showing the suspect''s phone was moving in that direction. However, cell tower evidence is approximate (covering areas of 0.5 to several miles), not GPS-precise. The Supreme Court ruled in Carpenter v. United States (2018) that obtaining long-term cell site location data requires a warrant, recognizing its profound privacy implications. Defense attorneys challenge tower evidence by noting that tower coverage areas overlap and environmental factors affect which tower a phone connects to.',
'{"options":[{"id":"a","text":"The suspect''s phone was moving toward the crime scene"},{"id":"b","text":"The suspect was definitely at the crime scene"},{"id":"c","text":"The cell towers were malfunctioning"},{"id":"d","text":"Someone else was carrying the suspect''s phone"}],"correctOptionId":"a"}', 3, ARRAY['forensics','digital','cell-tower']);

INSERT INTO lesson_applets (lesson_id, applet_id, sort_order) VALUES
('d4000000-0000-0000-0000-000000000011', 'a4110000-0000-0000-0000-000000000001', 1),
('d4000000-0000-0000-0000-000000000011', 'a4110000-0000-0000-0000-000000000002', 2),
('d4000000-0000-0000-0000-000000000011', 'a4110000-0000-0000-0000-000000000003', 3);

-- ‚îÄ‚îÄ‚îÄ Lesson 3.4 ‚Äî Investigation Review (Checkpoint) ‚îÄ‚îÄ‚îÄ
INSERT INTO course_lessons (id, unit_id, title, description, emoji, sort_order, xp_reward, is_checkpoint_review) VALUES
('d4000000-0000-0000-0000-000000000012', 'b4000000-0000-0000-0000-000000000003', 'Investigation Review', 'Test your knowledge of suspects, interrogation, and digital evidence.', '‚úÖ', 4, 15, true);

INSERT INTO applets (id, type, title, question, hint, explanation, content, difficulty, tags) VALUES
('a4120000-0000-0000-0000-000000000001', 'fill-blanks', 'Investigation Principles', 'Complete these key investigation principles.', 'Think about the murder triangle and interrogation reforms.',
'These principles represent hard-won lessons from decades of homicide investigation and wrongful conviction research. The murder triangle (means, motive, opportunity) provides structure. Starting with the victim''s inner circle reflects statistical reality. Recording interrogations became standard after too many false confession scandals. And the Carpenter decision established that digital privacy is a constitutional right ‚Äî even for murder suspects.',
'{"segments":[{"type":"text","content":"The three elements detectives look for in a suspect are means, motive, and "},{"type":"slot","slotId":"s1","correctAnswerId":"a1"},{"type":"text","content":". Statistically, about 64% of murder victims knew their "},{"type":"slot","slotId":"s2","correctAnswerId":"a2"},{"type":"text","content":". To prevent false confessions, interrogations should be "},{"type":"slot","slotId":"s3","correctAnswerId":"a3"},{"type":"text","content":" on video. Cell site location data requires a "},{"type":"slot","slotId":"s4","correctAnswerId":"a4"},{"type":"text","content":" since the Carpenter ruling."}],"answerBlocks":[{"id":"a1","content":"opportunity"},{"id":"a2","content":"killer"},{"id":"a3","content":"recorded"},{"id":"a4","content":"warrant"},{"id":"d1","content":"alibi"},{"id":"d2","content":"investigator"},{"id":"d3","content":"shortened"},{"id":"d4","content":"subpoena"}]}', 3, ARRAY['investigation','review','principles']),

('a4120000-0000-0000-0000-000000000002', 'mcq', 'Case Building', 'What legal standard must be met to arrest a murder suspect?', 'It''s more than a hunch but less than beyond reasonable doubt.',
'Probable cause is the legal standard for arrest ‚Äî it means there are facts and circumstances that would lead a reasonable person to believe the suspect committed the crime. This is a moderate standard ‚Äî higher than "reasonable suspicion" (enough for a brief stop) but lower than "beyond a reasonable doubt" (required for conviction). Detectives often have probable cause long before they have enough evidence to win at trial. Rushing an arrest before the case is trial-ready is one of the most common mistakes in homicide investigation ‚Äî it starts the speedy trial clock and may alert accomplices.',
'{"options":[{"id":"a","text":"Probable cause ‚Äî facts suggesting the suspect committed the crime"},{"id":"b","text":"Beyond reasonable doubt ‚Äî near certainty of guilt"},{"id":"c","text":"Reasonable suspicion ‚Äî a hunch based on experience"},{"id":"d","text":"Preponderance of evidence ‚Äî more likely than not"}],"correctOptionId":"a"}', 3, ARRAY['investigation','legal','arrest']);

INSERT INTO lesson_applets (lesson_id, applet_id, sort_order) VALUES
('d4000000-0000-0000-0000-000000000012', 'a4120000-0000-0000-0000-000000000001', 1),
('d4000000-0000-0000-0000-000000000012', 'a4120000-0000-0000-0000-000000000002', 2);

