INSERT INTO course_units (id, course_id, title, description, sort_order) VALUES
('b4000000-0000-0000-0000-000000000004', 'c0000000-0000-0000-0000-000000000005', 'Famous Cases', 'The investigations that changed forensic science ‚Äî from Jack the Ripper to the Golden State Killer.', 4);

-- ‚îÄ‚îÄ‚îÄ Lesson 4.1 ‚Äî The Golden State Killer ‚îÄ‚îÄ‚îÄ
INSERT INTO course_lessons (id, unit_id, title, description, emoji, sort_order, xp_reward) VALUES
('d4000000-0000-0000-0000-000000000013', 'b4000000-0000-0000-0000-000000000004', 'The Golden State Killer', 'How genetic genealogy caught a killer after 40 years.', 'üß¨', 1, 10);

INSERT INTO applets (id, type, title, question, hint, explanation, content, difficulty, tags) VALUES
('a4130000-0000-0000-0000-000000000001', 'ordering', 'How They Caught Him', 'Order the key steps that led to the identification of the Golden State Killer in 2018.', 'The breakthrough was using a consumer DNA database ‚Äî not a criminal one.',
'The Golden State Killer case revolutionized cold case investigation. After decades with no CODIS hit, investigator Paul Holes and genealogist Barbara Rae-Venter uploaded crime scene DNA to GEDmatch ‚Äî a free public genealogy database. They found distant relatives (3rd-4th cousins) who shared DNA segments with the killer. Genealogists built family trees working backward from these matches, identifying potential suspects. Joseph DeAngelo emerged as a candidate based on age, location, and law enforcement background. Covert DNA collection (from a discarded item) confirmed the match. He was arrested in April 2018 ‚Äî 44 years after his first known crimes.',
'{"items":[{"id":"i1","label":"Crime scene DNA had no match in CODIS","emoji":"‚ùå"},{"id":"i2","label":"DNA uploaded to public genealogy database (GEDmatch)","emoji":"üß¨"},{"id":"i3","label":"Distant relatives identified as partial matches","emoji":"üë®‚Äçüë©‚Äçüëß"},{"id":"i4","label":"Family trees built to narrow suspect pool","emoji":"üå≥"},{"id":"i5","label":"Covert DNA collected to confirm ‚Äî arrest made","emoji":"üöî"}],"correctOrder":["i1","i2","i3","i4","i5"],"direction":"top-down"}', 2, ARRAY['famous-cases','golden-state-killer','genealogy']),

('a4130000-0000-0000-0000-000000000002', 'mcq', 'Genetic Genealogy Ethics', 'What major privacy concern did the Golden State Killer case raise?', 'Think about who consented to have their DNA searched.',
'The case exposed a profound privacy issue: Joseph DeAngelo never submitted his DNA to GEDmatch ‚Äî his relatives did. By searching the database, investigators effectively conducted a genetic search on millions of people who never consented to law enforcement use of their DNA. Studies estimate that 60% of Americans of European descent can be identified through a third-cousin or closer match in these databases. GEDmatch later changed its privacy policy to require users to opt-in to law enforcement searches. The debate continues: is solving decades-old murders worth the erosion of genetic privacy for millions of innocent people?',
'{"options":[{"id":"a","text":"Innocent relatives'' DNA was searched without their consent"},{"id":"b","text":"The DNA evidence was too old to be reliable"},{"id":"c","text":"The killer was a former police officer"},{"id":"d","text":"The case took too long to solve"}],"correctOptionId":"a"}', 3, ARRAY['famous-cases','golden-state-killer','privacy']),

('a4130000-0000-0000-0000-000000000003', 'fill-blanks', 'GSK Case Facts', 'Complete the key facts about the Golden State Killer investigation.', 'Think about the timeline, the database, and the outcome.',
'The Golden State Killer case is now the textbook example of investigative genetic genealogy (IGG). DeAngelo committed crimes from 1974-1986 across California under multiple names (the Visalia Ransacker, the East Area Rapist, the Original Night Stalker). The 44-year gap between his crimes and arrest was bridged entirely by genetic genealogy. His arrest in 2018 opened the floodgates ‚Äî since then, over 500 cold cases have been solved using the same technique, including cases dating back to the 1960s.',
'{"segments":[{"type":"text","content":"The Golden State Killer was identified using "},{"type":"slot","slotId":"s1","correctAnswerId":"a1"},{"type":"text","content":" genealogy. Crime scene DNA was uploaded to "},{"type":"slot","slotId":"s2","correctAnswerId":"a2"},{"type":"text","content":". Investigators found distant "},{"type":"slot","slotId":"s3","correctAnswerId":"a3"},{"type":"text","content":" who shared DNA, then built family trees to identify Joseph DeAngelo, arrested in "},{"type":"slot","slotId":"s4","correctAnswerId":"a4"},{"type":"text","content":"."}],"answerBlocks":[{"id":"a1","content":"genetic"},{"id":"a2","content":"GEDmatch"},{"id":"a3","content":"relatives"},{"id":"a4","content":"2018"},{"id":"d1","content":"forensic"},{"id":"d2","content":"CODIS"},{"id":"d3","content":"witnesses"},{"id":"d4","content":"2020"}]}', 2, ARRAY['famous-cases','golden-state-killer','facts']);

INSERT INTO lesson_applets (lesson_id, applet_id, sort_order) VALUES
('d4000000-0000-0000-0000-000000000013', 'a4130000-0000-0000-0000-000000000001', 1),
('d4000000-0000-0000-0000-000000000013', 'a4130000-0000-0000-0000-000000000002', 2),
('d4000000-0000-0000-0000-000000000013', 'a4130000-0000-0000-0000-000000000003', 3);

-- ‚îÄ‚îÄ‚îÄ Lesson 4.2 ‚Äî The O.J. Simpson Case ‚îÄ‚îÄ‚îÄ
INSERT INTO course_lessons (id, unit_id, title, description, emoji, sort_order, xp_reward) VALUES
('d4000000-0000-0000-0000-000000000014', 'b4000000-0000-0000-0000-000000000004', 'The O.J. Simpson Case', 'The "Trial of the Century" ‚Äî how forensic evidence can be undermined.', '‚öñÔ∏è', 2, 10);

INSERT INTO applets (id, type, title, question, hint, explanation, content, difficulty, tags) VALUES
('a4140000-0000-0000-0000-000000000001', 'categorization-grid', 'Prosecution vs. Defense', 'Categorize each argument as belonging to the prosecution or the defense in the O.J. Simpson trial.', 'The prosecution had physical evidence. The defense attacked how it was collected.',
'The Simpson trial became a masterclass in how strong forensic evidence can be neutralized by attacking the process. The prosecution had blood evidence, DNA matches, a history of domestic violence, and a timeline. But the defense exposed that detective Mark Fuhrman had used racial slurs (undermining his credibility as the officer who found key evidence), argued evidence was planted, showed blood evidence was mishandled (the blood vial was carried around unsecured), and that the bloody glove didn''t fit. The jury acquitted ‚Äî demonstrating that in the American legal system, evidence quality matters less than jury perception of evidence integrity.',
'{"categories":[{"id":"prosecution","label":"Prosecution","emoji":"üë®‚Äç‚öñÔ∏è"},{"id":"defense","label":"Defense","emoji":"üõ°Ô∏è"}],"items":[{"id":"i1","text":"DNA evidence matched the defendant''s blood"},{"id":"i2","text":"Lead detective had documented racial bias"},{"id":"i3","text":"Blood drops formed a trail from crime scene to defendant''s home"},{"id":"i4","text":"Blood evidence was carried in an unsealed vial"},{"id":"i5","text":"History of domestic violence with 62 documented incidents"},{"id":"i6","text":"Bloody glove didn''t fit the defendant''s hand"}],"correctMapping":{"i1":"prosecution","i2":"defense","i3":"prosecution","i4":"defense","i5":"prosecution","i6":"defense"},"layout":"columns"}', 3, ARRAY['famous-cases','oj-simpson','trial']),

('a4140000-0000-0000-0000-000000000002', 'mcq', 'Lesson from Simpson', 'What is the most important forensic lesson from the O.J. Simpson acquittal?', 'Think about why the evidence failed to convince the jury.',
'The Simpson case taught the forensic world that having evidence is not enough ‚Äî the integrity of evidence collection and handling must be beyond reproach. Detective Fuhrman''s racism undermined the credibility of the bloody glove he claimed to find. The blood vial carried in a detective''s pocket for hours raised contamination questions. The prosecution''s DNA expert spent days explaining complex statistics that the jury found confusing. The case led to sweeping reforms: mandatory evidence handling protocols, diversity training in forensic labs, simplified expert testimony guidelines, and the understanding that forensic evidence must be presented in a way juries can understand and trust.',
'{"options":[{"id":"a","text":"Evidence collection integrity is as important as the evidence itself"},{"id":"b","text":"DNA evidence should never be used in murder trials"},{"id":"c","text":"Celebrity defendants always get acquitted"},{"id":"d","text":"Forensic evidence is unreliable in all cases"}],"correctOptionId":"a"}', 3, ARRAY['famous-cases','oj-simpson','forensic-lessons']),

('a4140000-0000-0000-0000-000000000003', 'thought-tree', 'Evidence Integrity', 'Make the right decisions to maintain evidence integrity ‚Äî lessons from the Simpson case.', 'Every shortcut creates an opening for the defense.',
'This tree directly mirrors the failures in the Simpson investigation. Properly sealing evidence containers prevents contamination claims. Having multiple officers witness evidence discovery prevents planting allegations. Documenting the chain of custody in real-time prevents gaps. Using clear, simple language in expert testimony ensures jury comprehension. And checking for investigator bias or conflicts of interest prevents credibility attacks. Each correct choice here represents a reform implemented after the Simpson verdict.',
'{"nodes":[{"id":"n1","question":"You collect a blood sample at the scene. How do you transport it?","leftChoice":{"id":"n1a","text":"Sealed evidence container, logged immediately"},"rightChoice":{"id":"n1b","text":"In your pocket ‚Äî you''ll log it at the station"},"correctChoiceId":"n1a"},{"id":"n2","question":"You find a critical piece of evidence (a glove). What do you do?","leftChoice":{"id":"n2a","text":"Have another officer witness and photograph the discovery"},"rightChoice":{"id":"n2b","text":"Bag it yourself ‚Äî speed is more important"},"correctChoiceId":"n2a"},{"id":"n3","question":"A defense attorney challenges the chain of custody. Your best defense?","leftChoice":{"id":"n3a","text":"Complete, timestamped log with every handler''s signature"},"rightChoice":{"id":"n3b","text":"Your sworn testimony that you remember handling it properly"},"correctChoiceId":"n3a"},{"id":"n4","question":"You''re explaining DNA evidence to the jury. What approach works best?","leftChoice":{"id":"n4a","text":"Simple analogies and clear visuals"},"rightChoice":{"id":"n4b","text":"Detailed statistical analysis with all the math"},"correctChoiceId":"n4a"},{"id":"n5","question":"You learn the lead detective has a documented bias. What do you do?","leftChoice":{"id":"n5a","text":"Replace them ‚Äî bias undermines the entire case"},"rightChoice":{"id":"n5b","text":"Keep them ‚Äî they know the case best"},"correctChoiceId":"n5a"}],"finalAnswer":"You maintained evidence integrity at every step. Sealed containers, witnessed discoveries, documented custody, clear jury communication, and removing biased investigators ‚Äî these reforms came directly from the Simpson case failures."}', 3, ARRAY['famous-cases','oj-simpson','evidence-integrity']);

INSERT INTO lesson_applets (lesson_id, applet_id, sort_order) VALUES
('d4000000-0000-0000-0000-000000000014', 'a4140000-0000-0000-0000-000000000001', 1),
('d4000000-0000-0000-0000-000000000014', 'a4140000-0000-0000-0000-000000000002', 2),
('d4000000-0000-0000-0000-000000000014', 'a4140000-0000-0000-0000-000000000003', 3);

-- ‚îÄ‚îÄ‚îÄ Lesson 4.3 ‚Äî Cold Cases Solved ‚îÄ‚îÄ‚îÄ
INSERT INTO course_lessons (id, unit_id, title, description, emoji, sort_order, xp_reward) VALUES
('d4000000-0000-0000-0000-000000000015', 'b4000000-0000-0000-0000-000000000004', 'Cold Cases Solved', 'How advances in technology crack cases that were unsolvable for decades.', '‚ùÑÔ∏è', 3, 10);

INSERT INTO applets (id, type, title, question, hint, explanation, content, difficulty, tags) VALUES
('a4150000-0000-0000-0000-000000000001', 'match-pairs', 'Technology Breakthroughs', 'Match each cold case breakthrough technology to the era when it first became available.', 'Think about when each technology was invented or became reliable enough for court.',
'Each decade brought forensic breakthroughs that cracked previously unsolvable cases. Fingerprint databases (AFIS) became operational in the 1980s, allowing automated matching for the first time. DNA profiling was first used in court in 1987 (the Colin Pitchfork case in England). CODIS (the national DNA database) went online in 1998. Genetic genealogy emerged in 2018 with the Golden State Killer. Advanced DNA phenotyping (predicting physical appearance from DNA) began in the 2020s. Each technology opened a new wave of cold case solutions.',
'{"leftItems":[{"id":"l1","text":"AFIS fingerprint database"},{"id":"l2","text":"DNA profiling in court"},{"id":"l3","text":"CODIS national DNA database"},{"id":"l4","text":"Investigative genetic genealogy"},{"id":"l5","text":"DNA phenotyping (appearance prediction)"}],"rightItems":[{"id":"r1","text":"1980s"},{"id":"r2","text":"1987"},{"id":"r3","text":"1998"},{"id":"r4","text":"2018"},{"id":"r5","text":"2020s"}],"correctPairs":{"l1":"r1","l2":"r2","l3":"r3","l4":"r4","l5":"r5"},"leftColumnLabel":"Technology","rightColumnLabel":"Era"}', 2, ARRAY['forensics','cold-cases','technology']),

('a4150000-0000-0000-0000-000000000002', 'mcq', 'First DNA Conviction', 'Who was the first person convicted of murder using DNA evidence?', 'It happened in England in 1988, and it also exonerated an innocent man.',
'Colin Pitchfork was the first person convicted of murder using DNA evidence, in 1988 in Leicestershire, England. Two teenage girls had been murdered in 1983 and 1986. Police conducted the world''s first mass DNA screening ‚Äî collecting samples from over 5,000 local men. Pitchfork initially avoided detection by convincing a coworker to submit a sample in his name. When that coworker was overheard bragging about it in a pub, police tested Pitchfork directly ‚Äî and got a match. Remarkably, the same DNA evidence first exonerated an innocent man, Richard Buckland, who had falsely confessed. This case proved DNA could both convict the guilty and free the innocent.',
'{"options":[{"id":"a","text":"Colin Pitchfork ‚Äî convicted in England in 1988"},{"id":"b","text":"Ted Bundy ‚Äî convicted in Florida in 1979"},{"id":"c","text":"Gary Ridgway ‚Äî the Green River Killer"},{"id":"d","text":"Timothy Spencer ‚Äî convicted in Virginia in 1988"}],"correctOptionId":"a"}', 3, ARRAY['forensics','cold-cases','DNA-history']),

('a4150000-0000-0000-0000-000000000003', 'ordering', 'Cold Case Revival', 'Order the steps detectives follow when reopening a cold case.', 'Start with what you have, then look for what''s changed since the original investigation.',
'Cold case review is systematic. First, the original case file is reviewed to understand what was done and what was missed. Then physical evidence is inventoried ‚Äî is it still preserved? Has it degraded? Next, detectives assess whether new technologies (DNA, genealogy, digital forensics) could be applied to existing evidence. Witnesses are re-interviewed ‚Äî people''s loyalties and fears change over time, and someone who was too scared to talk in 1985 may be willing now. Finally, new evidence is submitted to modern databases. Many cold cases are solved not by new evidence but by applying new technology to old evidence.',
'{"items":[{"id":"i1","label":"Review original case file and investigative notes","emoji":"üìÇ"},{"id":"i2","label":"Inventory physical evidence ‚Äî is it still preserved?","emoji":"üì¶"},{"id":"i3","label":"Assess if new technology applies to existing evidence","emoji":"üî¨"},{"id":"i4","label":"Re-interview witnesses (loyalties may have shifted)","emoji":"üó£Ô∏è"},{"id":"i5","label":"Submit evidence to modern databases (CODIS, NIBIN, IGG)","emoji":"üíª"}],"correctOrder":["i1","i2","i3","i4","i5"],"direction":"top-down"}', 2, ARRAY['forensics','cold-cases','procedure']);

INSERT INTO lesson_applets (lesson_id, applet_id, sort_order) VALUES
('d4000000-0000-0000-0000-000000000015', 'a4150000-0000-0000-0000-000000000001', 1),
('d4000000-0000-0000-0000-000000000015', 'a4150000-0000-0000-0000-000000000002', 2),
('d4000000-0000-0000-0000-000000000015', 'a4150000-0000-0000-0000-000000000003', 3);

-- ‚îÄ‚îÄ‚îÄ Lesson 4.4 ‚Äî Famous Cases Review (Checkpoint) ‚îÄ‚îÄ‚îÄ
INSERT INTO course_lessons (id, unit_id, title, description, emoji, sort_order, xp_reward, is_checkpoint_review) VALUES
('d4000000-0000-0000-0000-000000000016', 'b4000000-0000-0000-0000-000000000004', 'Famous Cases Review', 'Test your knowledge of landmark murder investigations.', '‚úÖ', 4, 15, true);

INSERT INTO applets (id, type, title, question, hint, explanation, content, difficulty, tags) VALUES
('a4160000-0000-0000-0000-000000000001', 'match-pairs', 'Case-Breakthrough Pairs', 'Match each famous case to the forensic breakthrough it''s most associated with.', 'Each case pioneered or highlighted a specific forensic technique.',
'These cases are milestones in forensic history. The Colin Pitchfork case established DNA as a courtroom tool. The O.J. Simpson case exposed the critical importance of evidence handling integrity. The Golden State Killer case proved genetic genealogy could solve the coldest of cold cases. The BTK Killer was caught through metadata on a floppy disk ‚Äî showing that digital forensics extends to every piece of technology. The Unabomber case demonstrated how linguistic analysis of written manifestos could narrow a suspect pool ‚Äî his brother recognized his writing style.',
'{"leftItems":[{"id":"l1","text":"Colin Pitchfork (1988)"},{"id":"l2","text":"O.J. Simpson (1995)"},{"id":"l3","text":"Golden State Killer (2018)"},{"id":"l4","text":"BTK Killer (2005)"},{"id":"l5","text":"Unabomber (1996)"}],"rightItems":[{"id":"r1","text":"First murder conviction using DNA evidence"},{"id":"r2","text":"Showed evidence handling is as crucial as evidence itself"},{"id":"r3","text":"Genetic genealogy solved a 40-year cold case"},{"id":"r4","text":"Caught through metadata on a floppy disk"},{"id":"r5","text":"Identified through linguistic analysis of writings"}],"correctPairs":{"l1":"r1","l2":"r2","l3":"r3","l4":"r4","l5":"r5"},"leftColumnLabel":"Case","rightColumnLabel":"Forensic Breakthrough"}', 3, ARRAY['famous-cases','review','breakthroughs']),

('a4160000-0000-0000-0000-000000000002', 'mcq', 'Wrongful Convictions', 'According to the Innocence Project, what is the leading cause of wrongful convictions in murder cases?', 'It''s not false confessions or bad forensics ‚Äî it''s something more fundamental.',
'Eyewitness misidentification is the leading cause of wrongful convictions, contributing to approximately 69% of the 375+ DNA exonerations as of 2023. Memory is far less reliable than people believe ‚Äî stress, cross-race identification, weapon focus (witnesses stare at the weapon rather than the face), and suggestive police lineups all contribute to misidentification. The second leading cause is junk science (faulty forensics like bite mark analysis or hair microscopy), followed by false confessions, and then informant testimony. These findings have led to reforms including double-blind lineups, recorded eyewitness confidence statements, and improved forensic standards.',
'{"options":[{"id":"a","text":"Eyewitness misidentification (~69% of exonerations)"},{"id":"b","text":"False confessions (~50% of exonerations)"},{"id":"c","text":"Corrupt prosecutors (~40% of exonerations)"},{"id":"d","text":"Planted evidence (~30% of exonerations)"}],"correctOptionId":"a"}', 3, ARRAY['famous-cases','wrongful-conviction','innocence']);

INSERT INTO lesson_applets (lesson_id, applet_id, sort_order) VALUES
('d4000000-0000-0000-0000-000000000016', 'a4160000-0000-0000-0000-000000000001', 1),
('d4000000-0000-0000-0000-000000000016', 'a4160000-0000-0000-0000-000000000002', 2);

-- =================================================================
-- UNIT 5 ‚Äî Justice & Ethics (The Bigger Picture)
-- =================================================================
INSERT INTO course_units (id, course_id, title, description, sort_order) VALUES
('b4000000-0000-0000-0000-000000000005', 'c0000000-0000-0000-0000-000000000005', 'Justice & Ethics', 'Beyond solving the case ‚Äî wrongful convictions, systemic bias, and the future of forensic science.', 5);

-- ‚îÄ‚îÄ‚îÄ Lesson 5.1 ‚Äî Wrongful Convictions ‚îÄ‚îÄ‚îÄ
INSERT INTO course_lessons (id, unit_id, title, description, emoji, sort_order, xp_reward) VALUES
('d4000000-0000-0000-0000-000000000017', 'b4000000-0000-0000-0000-000000000005', 'Wrongful Convictions', 'When the system fails ‚Äî how innocent people end up behind bars.', '‚öñÔ∏è', 1, 10);

INSERT INTO applets (id, type, title, question, hint, explanation, content, difficulty, tags) VALUES
('a4170000-0000-0000-0000-000000000001', 'ordering', 'Causes of Wrongful Conviction', 'Rank these causes of wrongful conviction from MOST to LEAST common, based on Innocence Project data.', 'Eyewitness mistakes are #1, followed by bad science.',
'The Innocence Project has documented over 375 DNA exonerations, revealing clear patterns. Eyewitness misidentification is the most common factor (~69%), driven by the inherent unreliability of human memory under stress. Faulty forensic science is second (~44%) ‚Äî including now-discredited techniques like bite mark analysis, hair microscopy comparison, and arson science. False confessions affect ~29% of cases. Government misconduct (prosecutorial or police) appears in ~44% but is harder to prove. Informant testimony (jailhouse snitches trading testimony for sentence reductions) rounds out the top five. Many cases involve multiple factors.',
'{"items":[{"id":"i1","label":"Eyewitness misidentification (~69%)","emoji":"üëÅÔ∏è"},{"id":"i2","label":"Faulty or misleading forensic science (~44%)","emoji":"üî¨"},{"id":"i3","label":"False confessions or admissions (~29%)","emoji":"üó£Ô∏è"},{"id":"i4","label":"Informant/jailhouse snitch testimony (~17%)","emoji":"ü§•"},{"id":"i5","label":"Inadequate defense representation","emoji":"üë®‚Äç‚öñÔ∏è"}],"correctOrder":["i1","i2","i3","i4","i5"],"direction":"top-down"}', 3, ARRAY['justice','wrongful-conviction','causes']),

('a4170000-0000-0000-0000-000000000002', 'mcq', 'Junk Science', 'Which of these forensic techniques has been largely discredited by the scientific community?', 'Think about which technique lacks a validated scientific basis.',
'Bite mark analysis has been largely discredited. A 2009 National Academy of Sciences report and a 2016 President''s Council of Advisors on Science and Technology (PCAST) report both concluded that bite mark comparison has no scientific basis for matching marks to a specific individual. Human skin distorts, swells, and changes ‚Äî making reliable comparison impossible. At least 26 people have been wrongfully convicted based on bite mark evidence. Despite this, some jurisdictions still allow it in court. Other questionable forensic methods include hair microscopy comparison (the FBI admitted in 2015 that its analysts gave flawed testimony in 96% of cases reviewed) and certain arson investigation techniques.',
'{"options":[{"id":"a","text":"Bite mark analysis ‚Äî no validated scientific basis"},{"id":"b","text":"DNA profiling ‚Äî too many false positives"},{"id":"c","text":"Fingerprint analysis ‚Äî completely unreliable"},{"id":"d","text":"Ballistic rifling comparison ‚Äî never accurate"}],"correctOptionId":"a"}', 3, ARRAY['justice','junk-science','forensics']),

('a4170000-0000-0000-0000-000000000003', 'match-pairs', 'Reform Measures', 'Match each wrongful conviction cause to the reform designed to prevent it.', 'Each reform targets a specific failure point in the system.',
'These reforms represent decades of advocacy by organizations like the Innocence Project and the National Registry of Exonerations. Double-blind lineups (where the administrator doesn''t know who the suspect is) prevent unconscious cues. Mandatory recording eliminates the "he said/she said" of interrogation disputes. Forensic science commissions evaluate whether techniques meet scientific standards before courts accept them. Tracking informant reliability prevents serial snitches from trading false testimony. Improved funding for public defenders addresses the reality that many wrongfully convicted defendants had lawyers who were overworked, underfunded, or incompetent.',
'{"leftItems":[{"id":"l1","text":"Eyewitness misidentification"},{"id":"l2","text":"False confessions"},{"id":"l3","text":"Junk forensic science"},{"id":"l4","text":"Unreliable informants"},{"id":"l5","text":"Inadequate defense"}],"rightItems":[{"id":"r1","text":"Double-blind lineups with confidence statements"},{"id":"r2","text":"Mandatory video recording of full interrogations"},{"id":"r3","text":"Forensic science commissions to validate methods"},{"id":"r4","text":"Tracking and disclosure of informant reliability"},{"id":"r5","text":"Increased funding for public defender offices"}],"correctPairs":{"l1":"r1","l2":"r2","l3":"r3","l4":"r4","l5":"r5"},"leftColumnLabel":"Problem","rightColumnLabel":"Reform"}', 3, ARRAY['justice','reform','wrongful-conviction']);

INSERT INTO lesson_applets (lesson_id, applet_id, sort_order) VALUES
('d4000000-0000-0000-0000-000000000017', 'a4170000-0000-0000-0000-000000000001', 1),
('d4000000-0000-0000-0000-000000000017', 'a4170000-0000-0000-0000-000000000002', 2),
('d4000000-0000-0000-0000-000000000017', 'a4170000-0000-0000-0000-000000000003', 3);

-- ‚îÄ‚îÄ‚îÄ Lesson 5.2 ‚Äî The Future of Forensics ‚îÄ‚îÄ‚îÄ
INSERT INTO course_lessons (id, unit_id, title, description, emoji, sort_order, xp_reward) VALUES
('d4000000-0000-0000-0000-000000000018', 'b4000000-0000-0000-0000-000000000005', 'The Future of Forensics', 'AI, rapid DNA, and the next generation of crime-solving technology.', 'üîÆ', 2, 10);

INSERT INTO applets (id, type, title, question, hint, explanation, content, difficulty, tags) VALUES
('a4180000-0000-0000-0000-000000000001', 'match-pairs', 'Emerging Technologies', 'Match each emerging forensic technology to what it promises to do.', 'Each technology addresses a current limitation in forensic science.',
'The next decade will transform forensic investigation. Rapid DNA machines (like ANDE) can produce a full DNA profile in under 2 hours at the crime scene ‚Äî versus the current days-to-weeks at a lab. DNA phenotyping can predict a suspect''s appearance (eye color, hair color, skin tone, face shape) from crime scene DNA. AI-powered video analysis can track suspects across thousands of hours of CCTV footage in minutes. Microbial forensics analyzes the unique bacteria on a person''s skin to place them at a scene. Digital twins create precise 3D reconstructions of crime scenes that jurors can virtually walk through.',
'{"leftItems":[{"id":"l1","text":"Rapid DNA machines"},{"id":"l2","text":"DNA phenotyping"},{"id":"l3","text":"AI video analysis"},{"id":"l4","text":"Microbial forensics"},{"id":"l5","text":"Digital twin crime scenes"}],"rightItems":[{"id":"r1","text":"Full DNA profile in under 2 hours at the scene"},{"id":"r2","text":"Predict suspect appearance from DNA alone"},{"id":"r3","text":"Track suspects across thousands of CCTV hours"},{"id":"r4","text":"Use unique skin bacteria to link person to location"},{"id":"r5","text":"Virtual reality walkthrough of the crime scene for jurors"}],"correctPairs":{"l1":"r1","l2":"r2","l3":"r3","l4":"r4","l5":"r5"},"leftColumnLabel":"Technology","rightColumnLabel":"Capability"}', 2, ARRAY['forensics','future','technology']),

('a4180000-0000-0000-0000-000000000002', 'thought-tree', 'Ethics of Future Tech', 'Navigate the ethical questions raised by emerging forensic technologies.', 'Balance crime-solving power with privacy and civil liberties.',
'Each emerging technology raises its own ethical dilemma. Genetic genealogy''s power comes at the cost of searching innocent relatives'' DNA. AI facial recognition has demonstrated racial bias ‚Äî misidentifying people of color at significantly higher rates. Rapid DNA at booking means DNA collection before conviction, raising presumption-of-innocence concerns. Predictive policing algorithms can reinforce existing racial disparities. The thread connecting all these issues is the tension between public safety and individual rights ‚Äî a balance that democratic societies must constantly negotiate.',
'{"nodes":[{"id":"n1","question":"Should police search public genealogy databases without the users'' consent?","leftChoice":{"id":"n1a","text":"Only with a court order for serious crimes"},"rightChoice":{"id":"n1b","text":"Yes ‚Äî if you upload DNA publicly, it''s fair game"},"correctChoiceId":"n1a"},{"id":"n2","question":"AI facial recognition has higher error rates for people of color. Should it be used?","leftChoice":{"id":"n2a","text":"Only after bias is reduced and with human verification"},"rightChoice":{"id":"n2b","text":"Yes ‚Äî imperfect tools are better than no tools"},"correctChoiceId":"n2a"},{"id":"n3","question":"Should DNA be collected from everyone arrested, even before conviction?","leftChoice":{"id":"n3a","text":"Only after conviction ‚Äî presumption of innocence matters"},"rightChoice":{"id":"n3b","text":"Yes ‚Äî it could solve cold cases immediately"},"correctChoiceId":"n3a"},{"id":"n4","question":"Predictive policing sends more officers to high-crime areas. What''s the concern?","leftChoice":{"id":"n4a","text":"It can reinforce racial bias in policing patterns"},"rightChoice":{"id":"n4b","text":"It''s purely data-driven and therefore unbiased"},"correctChoiceId":"n4a"},{"id":"n5","question":"What principle should guide ALL forensic technology adoption?","leftChoice":{"id":"n5a","text":"Scientific validation before courtroom use"},"rightChoice":{"id":"n5b","text":"Speed of adoption ‚Äî justice delayed is justice denied"},"correctChoiceId":"n5a"}],"finalAnswer":"You navigated the ethics correctly: court oversight for genealogy searches, bias reduction before AI deployment, conviction-based DNA collection, awareness of predictive policing bias, and scientific validation as the foundational principle. Technology must serve justice ‚Äî not replace it."}', 3, ARRAY['forensics','future','ethics']),

('a4180000-0000-0000-0000-000000000003', 'mcq', 'Clearance Rates', 'As of recent years, approximately what percentage of murders in the US are solved?', 'The rate has been declining for decades.',
'The US murder clearance rate has declined dramatically ‚Äî from about 90% in the 1960s to roughly 50% in recent years. This means that approximately half of all murders in America go unsolved. The decline is attributed to several factors: the decrease in "easy to solve" domestic murders (due to domestic violence interventions), the increase in stranger-on-stranger and gang-related violence, reduced community cooperation with police, overworked homicide units, and budget constraints. Cities like Chicago and Baltimore have clearance rates below 30%. This means improving forensic technology and investigation techniques isn''t just academic ‚Äî it could bring justice to thousands of victims whose cases currently go cold.',
'{"options":[{"id":"a","text":"About 50% ‚Äî roughly half go unsolved"},{"id":"b","text":"About 85% ‚Äî most murders are solved"},{"id":"c","text":"About 25% ‚Äî three-quarters go unsolved"},{"id":"d","text":"About 95% ‚Äî almost all are solved eventually"}],"correctOptionId":"a"}', 2, ARRAY['forensics','statistics','clearance-rate']);

